/**
 * используя композицию меняем свойства комбобокса,
 * чтобы брал данные из своего поля
 * и по другому показывал список выдачи
 */

import React, {Component} from 'react'
import Combo from 'components/combo'

class ComboExt extends Component {
   render () {
      return (
         <Combo
            id={this.props.id}
            width={this.props.width}
            type={this.props.type}
            placeholder={this.props.placeholder}
            checkAllEmptyPlaces={this.props.checkAllEmptyPlaces}
            alt={this.props.alt}
            extCountry="показать свой список Стран"
            //count = будет показывать все найденные но не более 15
         />
      )
   }
}

export default ComboExt
