import React, {Component} from 'react'
import {connect} from 'react-redux'
import './path.css'

class Path extends Component {
   onPath (val) {
      this.props.toPage (val);
   }

   render () {
      var {page} = this.props.parentState
      return (
         <div className={page === 4 ? "road path hide" : "road path"}>
            <span
               onClick={this.onPath.bind (this, 1)}
               className={page === 1 ? "blue" : ""}
            >Shipping</span>
            <span className="icon-arrow-right icon"/>
            <span
               onClick={this.onPath.bind (this, 2)}
               className={page === 2 ? "blue" : ""}
            >Billing</span>
            <span className="icon-arrow-right icon"/>
            <span
               onClick={this.onPath.bind (this, 3)}
               className={page === 3 ? "blue" : ""}
            >Payment</span>
         </div>
      )
   }
}

const mapStateToProps = state => ({state})
export default connect (mapStateToProps) (Path)