import React, {Component} from 'react'
import Combo from 'components/combo'

class FirstPage extends Component {
   constructor (props) {
      super (props)
      this.state = {
         checkAllEmptyPlaces: false
      }
   }

   onContinue () {
      this.setState ({checkAllEmptyPlaces: true})
      this.props.next (1);
   }

   render () {
      return (

         <div>
            <div className="road">
               Shipping
               <span className="icon-arrow-right"/>
               Billing
               <span className="icon-arrow-right"/>
               Payment
            </div>

            <h2 className="blue">Shipping info</h2>


            <h4 className="blue">Recipient</h4>
            <Combo
               id="1"
               type="fio"
               placeholder="Full Name"
               count="6"
               checkAllEmptyPlaces={this.state.checkAllEmptyPlaces}
               alt="Please enter recipient full name"
            />
            <div>
               <span className="floatleft">
                  <Combo
                     id="2"
                     width="200px"
                     placeholder="Daytime Phone"
                     checkAllEmptyPlaces={this.state.checkAllEmptyPlaces}
                     alt="Please enter recipient Daytime Phone"
                  />
               </span>
               <span>For deliver question only </span>
            </div>
            <h4 className="blue">Recipient</h4>
            <Combo
               id="3"
               type="address"
               placeholder="Street Address"
               checkAllEmptyPlaces={this.state.checkAllEmptyPlaces}
               alt="Please enter recipient Street Address"
            />
            <Combo
               id="4"
               placeholder="Apt, Suite, Bldg, Gate Code. (optional)"
            />
            <Combo
               id="5"
               type="address"
               placeholder="City"
               checkAllEmptyPlaces={this.state.checkAllEmptyPlaces}
               alt="Please enter recipient City"
            />
            <div>
               <Combo
                  id="6"
                  width="150px"
                  placeholder="Country"
                  checkAllEmptyPlaces={this.state.checkAllEmptyPlaces}
                  alt="Please enter recipient Country"
               />
               <Combo
                  id="7"
                  width="150px"
                  placeholder="ZIP"
                  checkAllEmptyPlaces={this.state.checkAllEmptyPlaces}
                  alt="Please enter recipient ZIP"
               />
            </div>

            <div
               className="button"
               onMouseDown={this.onContinue.bind (this)}>
               Continue
            </div>
         </div>
      )
   }
}

export default FirstPage