import React, {Component} from 'react'
import Combo from 'components/combo'
import {browserHistory} from "react-router";


class SecondPage extends Component {
   constructor (props) {
      super (props)
      this.state = {
         SecondPageCheck: false
      }
   }

   onContinue () {

      this.setState ({
         SecondPageCheck: true
      })


      // пока просто переход без проверки
      browserHistory.push ('/done');

   }

   render () {
      return (
         <div>
            <h2 className="blue">Payment</h2>
            <p>
               Yhis is a secure 1280bit SSL encrypted payment
            </p>
            <p className="blue">
               Cardholder Name
            </p>
            <Combo
               placeholder="Name as it appears on your card"
            />
            <p className="blue">
               Card Number
            </p>
            <Combo
               placeholder="XXXX XXXX XXXX XXXX XXXX"
            />
            <div>
               <span className="blue">
                  Expire Date
               </span>
               <span className="blue">
                  Security Code
               </span>
            </div>
            <div>
               <Combo
                  placeholder="MM/YY"
                  width="80px"
               />
               <Combo
                  placeholder=""
                  width="80px"
               />
            </div>
            <div className="button" onMouseDown={this.onContinue.bind (this)}>
               Pay Securely
            </div>
         </div>
      )
   }
}


export default SecondPage