import React, {Component} from 'react'
import Combo from 'components/combo'

class LeftArea extends Component{
   constructor (props){
      super(props)
      this.state={
         firstPageCheck:false
      }
   }
   onContinue(){
      console.log(" this.state.firstPageCheck = ", this.state.firstPageCheck);

      this.setState({
         firstPageCheck: true
      })
   }

   render(){
      return(
         <div>

            <div className="road">
               Shipping
               <span className="icon-arrow-right"/>
               Billing
               <span className="icon-arrow-right"/>
               Payment
            </div>

            <h2 className="blue">Shipping info</h2>


            <h4 className="blue">Billing contact</h4>
            <Combo
               type="fio"
               placeholder="Full Name"
               count="5"
               firstPageCheck={this.state.firstPageCheck}
               alt="Please enter recipient full name"
            />

            <div>
         <span className="floatleft">
            <Combo
               width="200px"
               type="email"
               placeholder="Daytime Phone"
               firstPageCheck={this.state.firstPageCheck}

            /></span>
               <span>For deliver question only </span>
            </div>


            <div>
               <Combo
                  width="150px"
                  placeholder="Full Name"
                  firstPageCheck={this.state.firstPageCheck}
               />
               <Combo
                  width="150px"
                  placeholder="Full Name"
                  firstPageCheck={this.state.firstPageCheck}
               />
            </div>

            <h4 className="blue">Billing address</h4>
            <Combo
               type="address"
               placeholder="Street Address"
               firstPageCheck={this.state.firstPageCheck}
               alt="Please enter recipient full name"
            />

            <div className="button" onMouseDown={this.onContinue.bind(this)}>
               Continue!
            </div>
         </div>
      )
   }
}



export default LeftArea