import R from 'ramda'
import {FETCH_ADD_PARAM_SUCCESS} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
   switch (type) {
      case FETCH_ADD_PARAM_SUCCESS:
         const params = R.indexBy (R.prop ('id'), payload)
         return R.merge (state, params)
      default:
         return state
   }
}