import R from 'ramda'
import {
   FETCH_COMBO_SUCCESS,
   FETCH_COUNTRY_SUCCESS
} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
   switch (type) {
      case FETCH_COMBO_SUCCESS:
         return R.values (R.pluck ('value', payload))
      case FETCH_COUNTRY_SUCCESS:
         return payload
      default:
         return state
   }
}